function onRequest(flow){
  if(flow.request.url.includes('/todo') && flow.request.method === 'POST'){
    try {
      var body = JSON.parse(flow.request.body);
      if(body.task){
        body.task = body.task.toLowerCase();
        setRequestBody(JSON.stringify(body));
      }
    } catch(e) {}
  }
}
